Pload_mW = []

for N in N_values:
    Z_refletida = (N ** 2) * Rload
    Z_total_mag = np.sqrt(Z_refletida**2 + Xs**2)
    I1 = V_fonte / Z_total_mag
    I2 = I1 * N
    P = (I2 ** 2) * Rload
    Pload_mW.append(P * 1000)

