# Zeq_LV = Zeq_HV / a^2
Zeq_LV_2_21 = Zeq_HV_2_21 / (a_2_21**2)

Req_LV_2_21 = Zeq_LV_2_21.real
Xeq_LV_2_21 = Zeq_LV_2_21.imag

